mapper.select:
/* eqpMgmtDAO.eqpMgmtMListTotCnt 장비관리 조회 TotalCnt */
select count(*) from ( select @ROWNUM := @ROWNUM + 1 AS rNum , a.EQP_SNO as eqpSno , a.EQP_NM as eqpNm , a.SR_NO as srNo , a.EQP_TYP as eqpTyp , CASE a.EQP_TYP WHEN 'C05001' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05001')/*데스크탑*/ WHEN 'C05002' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05002')/*노트북*/ WHEN 'C05003' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05003')/*모니터*/ WHEN 'C05004' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05004')/*기타*/ WHEN 'C05005' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05005')/*가구*/ WHEN 'C05006' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05006')/*프린터*/ END eqpTypNm , a.PURC_DT as purcDt , a.EXPR_DT as exprDt , a.MNFT_CO as mnftCo , a.MDL_NM as mdlNm , ( select ta.USER_NM from USER ta, EQP_USER_DTL tb where 1=1 and ta.USER_ID = tb.USER_ID and tb.EQP_SNO = a.EQP_SNO and ta.USE_YN = 'Y' and tb.USE_YN = 'Y' ) as userNm from EQUIPMENT_MGMT a where a.USE_YN = 'Y'
) q

mapper.insert:
    	/* regEqpMgmtRDtl 장비관리 장비 등록 */

    			INSERT EQUIPMENT_MGMT (
    												  EQP_SNO
													, EQP_BUY_DIV
													, EQP_NM
													, SR_NO
													, EQP_TYP
													, PURC_DT
													, EXPR_DT
													, GUAR_TRM
													, MNFT_CO
													, MDL_NM
													, MNFT_NAT

													, UNIT_AMT

													, DEPR_PRID
													, HLD_PLC
													, USE_YN
													, REG_DT
													, REGR_ID
													, UPD_DT
													, UPDR_ID

													, CPU
													, RAM
													, HDD_VOL
													, SSD_VOL
													, GRAPHICS


													, MNTR_SIZE
													, MNTR_RES

													, REMARKS
												)
										  VALUES(
										  			  ( SELECT CONCAT((
                                                        SELECT 
                                                        case 
                                                        	when #{eqpTyp} = 'C05001' then 'DSK'
                                                        	when #{eqpTyp} = 'C05002' then 'NTB'
                                                        	when #{eqpTyp} = 'C05003' then 'MNT'
                                                        	when #{eqpTyp} = 'C05004' then 'ETC'
                                                        	when #{eqpTyp} = 'C05005' then 'FNT'
                                                        	when #{eqpTyp} = 'C05006' then 'PRT'
                                                        end
                                                        from dual
                                                        ), LPAD(IFNULL(CAST(MAX(CAST(SUBSTR(A.EQP_SNO,4)  AS INT)) as char),0) + 1,7,'0')) FROM EQUIPMENT_MGMT A
                                                        where 1=1
                                                        and A.EQP_TYP = #{eqpTyp}
                                                        ) 
													, #{eqpBuyDiv}
													, #{eqpNm}
													, #{srNo}
													, #{eqpTyp}
													, REPLACE(#{purcDt},'-','')
													, REPLACE(#{exprDt},'-','')
													, #{guarTrm}
													, #{mnftCo}
													, #{mdlNm}
													, #{mnftNat}

													, REPLACE(#{unitAmt},',','')

													, #{deprPrid}
													, #{hldPlc}
													, 'Y'
													, now()
										            , #{session_userid}
										            , now()
										            , #{session_userid}

										            , #{cpu}
										            , #{ram}
										            , #{hddVol}
										            , #{ssdVol}
										            , #{graphics}


										            , #{mntrSize}
										            , #{mntrRes}

													, #{remarks}
												);

mapper.update:
/* updEqpMgmtUDtl 장비 관리 수정 */
UPDATE EQUIPMENT_MGMT
	SET 
	EQP_BUY_DIV	= #{eqpBuyDiv} 
	, EQP_NM = #{eqpNm} 
	, SR_NO	= #{srNo} 
	, EQP_TYP = #{eqpTyp} 
	, PURC_DT = REPLACE(#{purcDt},'-','')
	, EXPR_DT = REPLACE(#{exprDt},'-','')
	, GUAR_TRM = #{guarTrm} 
	, MNFT_CO = #{mnftCo} 
	, MDL_NM = #{mdlNm} 
	, MNFT_NAT = #{mnftNat} 
	, DEPR_PRID = #{deprPrid} 

	, UNIT_AMT = REPLACE(#{unitAmt},',','')

	, HLD_PLC = #{hldPlc}
	, CPU = #{cpu} 
	, RAM = #{ram}
	, HDD_VOL = #{hddVol}
	, SSD_VOL = #{ssdVol} 
	, GRAPHICS = #{graphics} 
	, MNTR_SIZE = #{mntrSize} 
	, MNTR_RES = #{mntrRes} 
	, REMARKS = #{remarks} 
	, UPD_DT = now()
	, UPDR_ID = #{session_userid}
WHERE USE_YN = 'Y'
	and EQP_SNO = #{ eqpSno }

mapper.insert:

    	INSERT INTO EQUIPMENT_MGMT 
		(
			EQP_SNO
			, EQP_BUY_DIV
			, EQP_NM
			, SR_NO
			, EQP_TYP
			, PURC_DT
			, EXPR_DT
			, GUAR_TRM
			, MNFT_CO
			, MDL_NM
			, MNFT_NAT
			, UNIT_AMT
			, DEPR_PRID
			, HLD_PLC
			, USE_YN
			, REG_DT
			, REGR_ID
			, UPD_DT
			, UPDR_ID
			, CPU
			, RAM
			, HDD_VOL
			, SSD_VOL
			, GRAPHICS
			, MNTR_SIZE
			, MNTR_RES
			, REMARKS
		)
		VALUES
		(
			( SELECT CONCAT((
              SELECT 
              case 
              	when #{eqpTyp} = 'C05001' then 'DSK'
              	when #{eqpTyp} = 'C05002' then 'NTB'
              	when #{eqpTyp} = 'C05003' then 'MNT'
              	when #{eqpTyp} = 'C05004' then 'ETC'
              	when #{eqpTyp} = 'C05005' then 'FNT'
              	when #{eqpTyp} = 'C05006' then 'PRT'
              end
              from dual
              ), LPAD(IFNULL(CAST(MAX(CAST(SUBSTR(A.EQP_SNO,4)  AS INT)) as char),0) + 1,7,'0')) FROM EQUIPMENT_MGMT A
              where 1=1
              and A.EQP_TYP = #{eqpTyp}
              )  
			, #{eqpBuyDiv}
			, #{eqpNm}
			, #{srNo}
			, #{eqpTyp}
			, REPLACE(#{purcDt},'-','')
			, REPLACE(#{exprDt},'-','')
			, #{guarTrm}
			, #{mnftCo}
			, #{mdlNm}
			, #{mnftNat}
			, CASE WHEN #{unitAmt} = '' OR #{unitAmt} IS NULL
			then 0
			else REPLACE(#{unitAmt},',','')
			END
			, #{deprPrid}
			, #{hldPlc}
			, 'Y'
			, now()
			, #{session_userid}
			, now()
			, #{session_userid}
			, #{cpu}
			, #{ram}
			, #{hddVol}
			, #{ssdVol}
			, #{graphics}
			, #{mntrSize}
			, #{mntrRes}
			, #{remarks}
		)
		ON DUPLICATE KEY 
		UPDATE EQP_NM = #{eqpNm}
				,EQP_BUY_DIV = #{eqpBuyDiv}
				,EQP_CNTR_NO = #{eqpCntrNo}
				,MNFT_CO = #{mnftCo}
				,MDL_NM = #{mdlNm}
				,MNFT_NAT = #{mnftNat}

				,UNIT_AMT = REPLACE(#{unitAmt},',','')


				,UNIT_AMT = NULL


				,PURC_QTY = REPLACE(#{purcQty},',','')


				,PURC_QTY = NULL


				,SUM_AMT = REPLACE(#{sumAmt},',','')


				,SUM_AMT = NULL


				,DSTB_QTY = REPLACE(#{dstbQty},',','')


				,DSTB_QTY = NULL

				,DEPR_PRID = #{deprPrid}
				,PAY_DIV = #{payDiv}
				,UPD_DT = NOW()
				,UPDR_ID = #{session_userid}

mapper.select:
/* eqpMgmtDAO.eqpMgmtMListPop 장비관리 조회 팝업 */
SELECT * FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM , A.EQP_SNO , A.EQP_NM , A.SR_NO , A.EQP_TYP , FN_GET_CODENM(A.EQP_TYP) AS EQP_TYP_NM , A.MDL_NM , A.MNFT_CO , A.PURC_DT , A.REG_DT FROM EQUIPMENT_MGMT A ,(SELECT @ROWNUM := 0) R WHERE 1=1 AND A.USE_YN = 'Y' AND NOT EXISTS ( SELECT EUD.EQP_SNO FROM EQP_USER_DTL EUD WHERE EUD.EQP_SNO = A.EQP_SNO AND EUD.PGS_STAT = 'C03001' )
)A LIMIT #{pageStart}, #{perPageNum}

/* eqpMgmtDAO.eqpMgmtMList2 장비관리 조회 */
select @ROWNUM := @ROWNUM + 1 AS rNum , a.EQP_SNO as eqpSno , a.EQP_NM as eqpNm , a.SR_NO as srNo , a.EQP_TYP as eqpTyp , CASE a.EQP_TYP WHEN 'C05001' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05001')/*데스크탑*/ WHEN 'C05002' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05002')/*노트북*/ WHEN 'C05003' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05003')/*모니터*/ WHEN 'C05004' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05004')/*기타*/ WHEN 'C05005' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05005')/*가구*/ WHEN 'C05006' THEN (SELECT CD_NM FROM CODE_DTL WHERE CD = 'C05006')/*프린터*/ END eqpTypNm , a.PURC_DT as purcDt , a.EXPR_DT as exprDt , a.MNFT_CO as mnftCo , a.MDL_NM as mdlNm , a.REMARKS , ( select ta.USER_NM from USER ta, EQP_USER_DTL tb where 1=1 and ta.USER_ID = tb.USER_ID and tb.EQP_SNO = a.EQP_SNO and ta.USE_YN = 'Y' and tb.USE_YN = 'Y' ) as userNm from EQUIPMENT_MGMT a where a.USE_YN = 'Y'
order by a.REG_DT DESC LIMIT #{offset}, #{perPageNum}
