mapper.select:
/* adminUserDAO.getPwdErrCnt 비밀번호 오류 회수 조회 */


SELECT IFNULL(PWD_ERR_CNT,0)
FROM USER
WHERE USER_ID = #{userId}

mapper.update:
/* adminUserDAO.updatePwdErrCnt 비밀번호 오류 증가 */
UPDATE USER
SET PWD_ERR_CNT = #{pwdErrCnt}
WHERE USER_ID = #{userId}

mapper.sql:

             AND A.USER_ID LIKE  CONCAT('%' , #{schUserId} , '%')


             AND A.USER_NM LIKE  CONCAT('%' , #{schUserNm} , '%')


             AND A.USER_GB = #{schUserGb}  


             AND A.REG_STATUS = #{schRegStatus}  


             AND A.USER_ID LIKE  CONCAT('%' , #{schUserId} , '%')


             AND A.USE_YN = #{schUseYn}


         	 AND EXISTS (
			  				SELECT TB.INSTT_CD
			  				  FROM INSTT TB
			  				 WHERE TB.INSTT_CD = A.INSTT_CD 
			  				   AND (TB.INSTT_NM LIKE CONCAT('%' , #{schInsttNm} , '%') OR TB.DEPT_NM LIKE CONCAT('%' , #{schInsttNm} , '%') OR TB.TEAM_NM LIKE CONCAT('%' , #{schInsttNm} , '%'))
			  			 )

            AND A.USER_GB <> 'C01999'

mapper.select:
/* fsysUserDAO.sysUserSelList */
SELECT * FROM ( SELECT A.USER_ID , A.USER_NM , A.USER_GB , IFNULL( A.TEL_NO,'') AS TEL_NO , IFNULL(A.HP_TEL_NO,'') AS HP_TEL_NO , IFNULL(A.EMAIL,'') AS EMAIL , A.REG_DT , DATE_FORMAT(A.REG_DT, '%Y-%m-%d') AS DISP_REG_DT , A.PWD_CHG_DT , IFNULL(DATE_FORMAT(A.PWD_CHG_DT, '%Y-%m-%d'),'') AS DISP_PWD_CHG_DT , IFNULL(A.PWD_ERR_CNT,0) AS PWD_ERR_CNT , A.REG_STATUS , CASE WHEN A.REG_STATUS = 'Y' THEN '승인' ELSE '미승인' END AS REG_STATUS_NM , A.INSTT_CD , (SELECT CONCAT(DI.DEPT_NM,' ',DI.TEAM_NM) FROM INSTT DI WHERE DI.INSTT_CD = A.INSTT_CD) AS INSTT_CD_NM , A.USE_YN , CASE WHEN A.USE_YN = 'Y' THEN '사용' ELSE '미사용' END AS USE_YN_NM , @ROWNUM := @ROWNUM + 1 AS RNUM FROM USER A, (SELECT @ROWNUM := 0) R WHERE 1=1
)A ORDER BY A.REG_DT DESC LIMIT #{pageStart}, #{perPageNum}

mapper.insert:
    	/* regFsysUserRDtl */

    	INSERT INTO USER (
    							   USER_ID
    							 , REG_STATUS
    							 , USER_GB
    							 , PWD
    							 , USER_NM
    							 , TEL_NO
    							 , HP_TEL_NO
    							 , EMAIL



    							 , PWD_FIND_QUES
    							 , PWD_FIND_ASW
    							 , INSTT_CD
    							 , USE_YN
    							 , REG_DT
    							 , REGR_ID
    							 , UPD_DT
    							 , UPDR_ID
    						  )
					   VALUES (
					                #{userId}
					              , #{regStatus}
					              , #{userGb}
					              , #{pwd}
					              , #{userNm}
					              , #{telNo}
					              , #{hpTelNo}
					              , #{email}



					              , #{pwdFindQues}
					              , #{pwdFindAsw}
					              , #{insttCd}
					              , 'Y'
					              , now()
					              , #{regrId}
					              , now()
					              , #{regrId}
					          );

mapper.select:
           SELECT COUNT(A.USER_ID) AS RESULT_CNT 
			 FROM USER A
			WHERE A.USER_ID = #{userId}

mapper.update:
		/* updFsysUserUDtl 사용자 정보 수정 */
        UPDATE USER
           SET EMAIL           = #{email}

             , USER_NM         = #{userNm}    /* 관리자가 회원수정시 이름까지 가능*/

             , TEL_NO          = #{telNo}
             , HP_TEL_NO       = #{hpTelNo}
             , UPDR_ID         = #{userId}
             , UPD_DT          = NOW()
             , PWD_FIND_QUES   = #{pwdFindQues}
             , PWD_FIND_ASW    = #{pwdFindAsw}
             , REG_STATUS = #{regStatus}
             , USE_YN          = #{useYn}

             , PWD             = #{pwd}
             , PWD_CHG_DT      = DATE_FORMAT(DATE_ADD(DATE_FORMAT(NOW(), '%Y%m%d'),INTERVAL -100 DAY),'%Y%m%d') /* 관리자가 비번초기화시 세팅*/






         WHERE USER_ID = #{userId}

mapper.delete:
	/* delFsysUserUDtl 사용자 삭제 */

    DELETE FROM USER
    WHERE 1 = 1
      AND USER_ID = #{userId}

	/* delFsysDeptUserRDtl 부서별 사용자 삭제 */

    DELETE FROM DEPT_USER
    WHERE 1 = 1
      AND INSTT_CD = #{insttCd}
      AND USER_ID = #{userId}

mapper.insert:
    	/* regFsysUserRDtl */

    	INSERT INTO DEPT_USER (
    							   		  INSTT_CD
										, USER_ID 
										, USE_YN
										, REG_DT
										, REGR_ID
										, UPD_DT
										, UPDR_ID
    						  	   )
						   VALUES (
						                #{insttCd}
						              , #{userId}
						              , 'Y'
						              , now()
						              , #{regrId}
						              , now()
						              , #{regrId}
						          );

mapper.update:
/* fsysUserDAO.updFsysUserUserUpdPDtlPop 사용자 정보 수정 */
UPDATE USER
   SET EMAIL           = #{email}
     , USER_NM         = #{userNm}
     , TEL_NO          = #{telNo}
     , UPDR_ID         = #{session.userid}
     , UPD_DT          = NOW()
 WHERE USER_ID = #{userId}

mapper.select:
/* adminUser.selectUserPw 비밀번호 변경 전 이전 비밀번호 일치 여부 확인 */
SELECT COUNT(*) AS CNT
  FROM USER
 WHERE USER_ID = #{userId}

   AND PWD     = #{chkPwd}

mapper.update:

/* fsysUserDAO.chgPwFsysUserUserUpdPDtlPop 비밀번호 수정 */
UPDATE USER
   SET PWD             = #{pwd}
     , PWD_CHG_DT      = DATE_FORMAT(now(), '%Y%m%d')

     , PWD_FIND_QUES   = #{pwdFindQues} 


     , PWD_FIND_ASW    = #{pwdFindAsw}

     , UPDR_ID         = #{userId}
     , UPD_DT          = NOW()
 WHERE USER_ID = #{userId}

/* adminUser.updateUserPwDate 3개월 후 비밀번호 변경 */

UPDATE USER
   SET PWD_CHG_DT  = TO_CHAR(SYSDATE + 90 , 'YYYYMMDD')
      ,UPD_DT      = SYSDATE
      ,UPDR_ID     = #{userId}
 WHERE USER_ID     = #{userId}
